// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  if (item.json.document.metadata.from == "Asana") 
    item.json.document.metadata.id = item.json.document.metadata.gid
}

return $input.all();

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const project = item.json;

  delete project._id;
  delete project.gid;
  delete project.color;
  delete project.default_access_level;
  delete project.default_view;
  delete project.minimum_access_level_for_customization;
  delete project.minimum_access_level_for_sharing;
  delete project.permalink_url;
  delete project.privacy_setting;
  delete project.resource_type;
  delete project.workspace;
  delete project.html_notes;
  delete project.created_from_template;
  delete project['from'];
  delete project.type;
  delete project.client;
  delete project.custom_fields;
  delete project.custom_field_settings;

  project.team = project.team ? project.team.name : '';
  project.owner = project.owner ? project.owner.name : '';
  if (project.followers != undefined)
    project.followers = project.followers.map(follower => follower.name);
  if (project.members != undefined)
    project.members = project.members.map(member => member.name);
}

return $input.all();

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const review = item.json;
  delete review._id;
  delete review.content;
  delete review.client;
  delete review.from;
  delete review.children;
  delete review.id;
}

if ($('Switch').first().json.output.requirement === "create_review") {
  return $input.all();
} else {
  return $input.all().slice(-5);
}

// Get all input items from the previous node
const tasksRaw = $input.all();

// Get the 'requirement' value from the 'Switch' node.
// This is done once to avoid repeatedly accessing it inside the loop.
const requirement = $('Switch').first().json.output.requirement;

// --- Edge Case Handling ---
// If there are no items, or only one empty item, return an empty structure.
if (tasksRaw.length === 0 || (tasksRaw.length === 1 && Object.keys(tasksRaw[0].json).length === 0)) {
  return [{
    json: {
      active: [],
      completed: []
    }
  }];
}

// --- Main Logic ---

const activeTasks = [];
const completedTasks = [];

// Loop over input items once to process and categorize them
for (const taskWrapper of tasksRaw) {
  // Get the main JSON object from the item
  const task = taskWrapper.json;

  // Delete unnecessary properties for a cleaner object
  delete task.html_notes;
  delete task.hearts;
  delete task.likes;
  delete task.projects;
  delete task.workspace;
  delete task.created_by;
  delete task.client;
  delete task.from;
  delete task.type;

  // Simplify complex fields
  task.assignee = task.assignee ? task.assignee.name : "";

  // Use .map() to transform arrays of objects into simpler arrays
  // Check if the array exists and is not empty before mapping
  if (task.custom_fields && task.custom_fields.length > 0) {
    task.custom_fields = task.custom_fields.map(cf => ({
      name: cf.name,
      value: cf.display_value
    }));
  }

  if (task.followers && task.followers.length > 0) {
    task.followers = task.followers.map(follower => follower.name);
  }

  if (task.memberships && task.memberships.length > 0) {
    task.memberships = task.memberships.map(mb => ({
      project: mb.project.name,
      section: mb.section.name
    }));
  }

  if (task.tags && task.tags.length > 0) {
    task.tags = task.tags.map(tag => tag.name);
  }

  // Conditional processing based on the requirement
  if (requirement === "create_review") {
    // Use .reduce() to build a single string from the stories array
    const storyText = task.stories.reduce((acc, story) => {
      const author = story.created_by ? story.created_by.name : "Nobody";
      const text = story.text || ""; // Handle cases where text is missing
      // Use template literals for clean string formatting
      return `${acc}Story at: ${story.created_at} by ${author}\n${text}\n\n`;
    }, ""); // Start with an empty string
    task.stories = storyText;
  } else {
    // If requirement is not 'create_review', delete these properties
    delete task.attachments;
    delete task.stories;
  }

  // Categorize the processed task based on its 'completed' status
  if (task.completed) {
    completedTasks.push(task);
  } else {
    activeTasks.push(task);
  }
}

// --- Sorting ---

// Sort the active tasks by due date (items without a date go to the end)
activeTasks.sort((a, b) => {
  // Provide a far-future date for null/undefined values to sort them last
  const dueA = a.due_on || '3000-12-31';
  const dueB = b.due_on || '3000-12-31';
  // localeCompare is a reliable way to sort ISO date strings
  return dueA.localeCompare(dueB);
});

// Sort the completed tasks by completion date (most recent first)
completedTasks.sort((a, b) => {
  // Using localeCompare, b comes before a for descending order
  return b.completed_at.localeCompare(a.completed_at);
});

// --- Final Output Preparation ---

// Determine the number of completed tasks to return based on the requirement
const limit = requirement === "create_task" ? 50 : 10;

// Slice the sorted completed tasks array to get only the most recent 'limit' items
const finalCompletedTasks = completedTasks.slice(0, limit);


// Return the final structured object in the format N8N expects.
// It should be an array of items, where each item has a 'json' property.
// Here, we return a single item containing both lists.
return [{
  json: {
    active: activeTasks,
    completed: finalCompletedTasks
  }
}];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const review = item.json;
  delete review._id;
  delete review.content;
  delete review.client;
  delete review.from;
  delete review.children;
  delete review.id;
}

if ($('Switch').first().json.output.requirement === "create_review") {
  return $input.all();
} else {
  return $input.all().slice(-5);
}

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  if (item.json["Project Name"].startsWith($("Extract Topic").first().json.output.client_id + ".")) return [{'allow': false, 'client_id': $("Extract Topic").first().json.output.client_id}]
}

return $("Extract Topic").first().json;

